// Main App
export default function BodyMapApp({ therapistName = "Your Therapist", onSubmit = null, getLastSession = null }) {
  const [screen, setScreen] = useState("welcome");
  const [mode, setMode] = useState("focus");
  const [clientInfo, setCI] = useState({ name: "", contact: "" });
  const [bodyMap, setBM] = useState({});
  const [prefs, setPrefs] = useState({ ...DEFAULT_PREFS });
  const [notes, setNotes] = useState("");
  const [consent, setConsent] = useState(false);
  const [sessions, setSessions] = useState([]);
  const [lastSession, setLastSession] = useState(null);
  const [checkingReturn, setCheckingReturn] = useState(false);

  useEffect(() => {
    const l = document.createElement("link");
    l.href =
      "https://fonts.googleapis.com/css2?family=Cormorant+Garamond:ital,wght@0,400;0,500;0,600;0,700;1,400&family=Nunito:wght@400;500;600;700;800&display=swap";
    l.rel = "stylesheet";
    document.head.appendChild(l);
    try {
      setSessions(JSON.parse(localStorage.getItem("bodymap-sessions") || "[]"));
    } catch {}
    return () => {
      try {
        document.head.removeChild(l);
      } catch {}
    };
  }, []);

  const toggle = (id) =>
    setBM((p) => {
      if (!p[id]) return { ...p, [id]: mode };
      if (p[id] === mode) {
        const n = { ...p };
        delete n[id];
        return n;
      }
      return { ...p, [id]: mode };
    });

  const handlePreFill = (last) => {
    setBM({ ...last.bodyMap });
    setPrefs({ ...DEFAULT_PREFS, ...last.prefs });
    if (last.clientInfo) setCI({ ...last.clientInfo });
    setScreen("front");
  };

  const onStart = (info) => {
    setCI(info);
    setLastSession(null);
    setScreen("front");
    if (getLastSession && info.contact) {
      setTimeout(() => {
        getLastSession(info.contact).then(last => {
          if (last) setLastSession(last);
        }).catch(() => {});
      }, 100);
    }
  };

  const applyPrefill = (last) => {
    setBM(last.bodyMap || {});
    setPrefs(prev => ({ ...prev, ...(last.prefs || {}) }));
  };

  const handleApply = () => {
    const p = buildPattern(sessions);
    if (!p) return;
    const dm = {};
    p.topFocus.slice(0, 3).forEach((f) => {
      const r = ALL_REGIONS.find((r) => r.label === f.label);
      if (r) dm[r.id] = "focus";
    });
    p.topAvoid.slice(0, 2).forEach((a) => {
      const r = ALL_REGIONS.find((r) => r.label === a.label);
      if (r) dm[r.id] = "avoid";
    });
    setBM(dm);
    setPrefs((prev) => ({
      ...prev,
      pressure: p.avgPressure,
      goal: p.topGoal || prev.goal,
    }));
  };

  const handleSend = () => {
    const { hasEarned } = getLoyalty(sessions);
    const s = {
      id: Date.now(),
      date: new Date().toISOString(),
      clientInfo,
      bodyMap,
      prefs,
      notes,
      therapistNotes: "",
      isFreeSession: hasEarned,
    };
    const upd = [s, ...sessions].slice(0, 50);
    setSessions(upd);
    try {
      localStorage.setItem("bodymap-sessions", JSON.stringify(upd));
    } catch {}

    // If onSubmit prop is provided, call it with intake data
    if (onSubmit) {
      // Extract focus and avoid areas
      const frontFocus = Object.keys(bodyMap).filter(k => k.startsWith('f-') && bodyMap[k] === 'focus');
      const frontAvoid = Object.keys(bodyMap).filter(k => k.startsWith('f-') && bodyMap[k] === 'avoid');
      const backFocus = Object.keys(bodyMap).filter(k => k.startsWith('b-') && bodyMap[k] === 'focus');
      const backAvoid = Object.keys(bodyMap).filter(k => k.startsWith('b-') && bodyMap[k] === 'avoid');

      onSubmit({
        clientName: clientInfo.name,
        clientPhone: clientInfo.contact,
        clientEmail: null,
        frontFocus,
        frontAvoid,
        backFocus,
        backAvoid,
        pressure: prefs.pressure,
        goal: prefs.goal,
        tableTemp: prefs.tableTemp,
        roomTemp: prefs.roomTemp,
        music: prefs.music,
        lighting: prefs.lighting,
        conversation: prefs.conversation,
        draping: prefs.draping,
        oilPref: prefs.oilPref,
        medFlag: prefs.medFlag,
        medNote: prefs.medNote || null,
        notes: notes || null
      });
    } else {
      // Default behavior - show summary screen
      setScreen("summary");
    }
  };

  const reset = () => {
    setBM({});
    setNotes("");
    setConsent(false);
    setPrefs({ ...DEFAULT_PREFS });
    setScreen("welcome");
  };

  const screens = {
    welcome: (
      <WelcomeScreen
        onStart={onStart}
        sessions={sessions}
        onHistory={() => setScreen("history")}
        checkingReturn={checkingReturn}
        onPreFill={handlePreFill}
      />
    ),
    front: (
      <div>
        {lastSession && (
          <div style={{ background: "#FDF6E8", border: "1.5px solid #D4B070", padding: "12px 16px", display: "flex", justifyContent: "space-between", alignItems: "center", gap: 10 }}>
            <div>
              <p style={{ fontFamily: "'Nunito', sans-serif", fontSize: 13, fontWeight: 800, color: "#B87840", margin: 0 }}>âœ¦ Welcome back{clientInfo?.name ? `, ${clientInfo.name.split(" ")[0]}` : ""}!</p>
              <p style={{ fontFamily: "'Nunito', sans-serif", fontSize: 11, color: "#7A9485", margin: "2px 0 0" }}>Tap "Use Last Session" to pre-fill your preferences</p>
            </div>
            <button onClick={() => applyPrefill(lastSession)} style={{ background: "#B87840", color: "#fff", border: "none", padding: "8px 14px", borderRadius: 50, fontFamily: "'Nunito', sans-serif", fontSize: 12, fontWeight: 800, cursor: "pointer", whiteSpace: "nowrap" }}>
              Use Last Session
            </button>
          </div>
        )}
        <BMScreen
          title="Front of Body"
          subtitle="Tap any area to mark as focus or avoid"
          step={1}
          regions={FRONT_REGIONS}
          selections={bodyMap}
          onToggle={toggle}
          mode={mode}
          setMode={setMode}
          onNext={() => setScreen("back")}
          onBack={null}
          sessions={[]}
          onApply={null}
        />
      </div>
    ),
    back: (
      <div>
        {lastSession && (
          <div style={{ background: "#FDF6E8", border: "1.5px solid #D4B070", padding: "12px 16px", display: "flex", justifyContent: "space-between", alignItems: "center", gap: 10 }}>
            <div>
              <p style={{ fontFamily: "'Nunito', sans-serif", fontSize: 13, fontWeight: 800, color: "#B87840", margin: 0 }}>âœ¦ Welcome back{clientInfo?.name ? `, ${clientInfo.name.split(" ")[0]}` : ""}!</p>
              <p style={{ fontFamily: "'Nunito', sans-serif", fontSize: 11, color: "#7A9485", margin: "2px 0 0" }}>Tap "Use Last Session" to pre-fill your preferences</p>
            </div>
            <button onClick={() => applyPrefill(lastSession)} style={{ background: "#B87840", color: "#fff", border: "none", padding: "8px 14px", borderRadius: 50, fontFamily: "'Nunito', sans-serif", fontSize: 12, fontWeight: 800, cursor: "pointer", whiteSpace: "nowrap" }}>
              Use Last Session
            </button>
          </div>
        )}
        <BMScreen
          title="Back of Body"
          subtitle="Continue marking focus and avoid areas"
          step={2}
          regions={BACK_REGIONS}
          selections={bodyMap}
          onToggle={toggle}
          mode={mode}
          setMode={setMode}
          onNext={() => setScreen("prefs")}
          onBack={() => setScreen("front")}
          sessions={[]}
          onApply={() => {}}
        />
      </div>
    ),
    prefs: (
      <div>
        {lastSession && (
          <div style={{ background: "#FDF6E8", border: "1.5px solid #D4B070", padding: "12px 16px", display: "flex", justifyContent: "space-between", alignItems: "center", gap: 10 }}>
            <div>
              <p style={{ fontFamily: "'Nunito', sans-serif", fontSize: 13, fontWeight: 800, color: "#B87840", margin: 0 }}>âœ¦ Welcome back{clientInfo?.name ? `, ${clientInfo.name.split(" ")[0]}` : ""}!</p>
              <p style={{ fontFamily: "'Nunito', sans-serif", fontSize: 11, color: "#7A9485", margin: "2px 0 0" }}>Tap "Use Last Session" to pre-fill your preferences</p>
            </div>
            <button onClick={() => applyPrefill(lastSession)} style={{ background: "#B87840", color: "#fff", border: "none", padding: "8px 14px", borderRadius: 50, fontFamily: "'Nunito', sans-serif", fontSize: 12, fontWeight: 800, cursor: "pointer", whiteSpace: "nowrap" }}>
              Use Last Session
            </button>
          </div>
        )}
        <PrefScreen
          prefs={prefs}
          setPrefs={setPrefs}
          notes={notes}
          setNotes={setNotes}
          consent={consent}
          setConsent={setConsent}
          onNext={handleSend}
          onBack={() => setScreen("back")}
        />
      </div>
    ),
    summary: (
      <SummaryScreen
        clientInfo={clientInfo}
        bodyMap={bodyMap}
        onViewTherapist={() => setScreen("therapist")}
        onReset={reset}
      />
    ),
    therapist: (
      <TherapistView
        clientInfo={clientInfo}
        bodyMap={bodyMap}
        prefs={prefs}
        notes={notes}
        sessions={sessions}
        onBack={() => setScreen("summary")}
        onReview={() => setScreen("review")}
      />
    ),
    review: <ReviewScreen clientInfo={clientInfo} onDone={reset} />,
    history: (
      <HistoryScreen
        sessions={sessions}
        clientName={clientInfo.name}
        onBack={() => setScreen("welcome")}
        onNewSession={reset}
      />
    ),
  };

  return (
    <>
      <style>{`
        .intake-desktop-bg { min-height:100vh; background:linear-gradient(155deg,#2A5741,#4A8B6B); display:flex; align-items:center; justify-content:center; padding:40px 20px; }
        .intake-phone-frame { width:390px; min-height:780px; border-radius:48px; background:#F0EAD9; box-shadow:0 40px 80px rgba(0,0,0,0.4),0 0 0 12px #1A1A2E,0 0 0 14px #333; overflow:hidden; position:relative; }
        .intake-notch { width:120px; height:28px; background:#1A1A2E; border-radius:0 0 20px 20px; position:absolute; top:0; left:50%; transform:translateX(-50%); z-index:10; }
        .intake-content { padding-top:28px; min-height:100%; background:#F0EAD9; }
        @media(max-width:767px) { .intake-desktop-bg { background:#F0EAD9; padding:0; align-items:flex-start; } .intake-phone-frame { width:100%; min-height:100vh; border-radius:0; box-shadow:none; } .intake-notch { display:none; } .intake-content { padding-top:0; } }
      `}</style>
      <div className="intake-desktop-bg">
        <div className="intake-phone-frame">
          <div className="intake-notch" />
          <div className="intake-content" style={{fontFamily:F.body}}>
            {screens[screen] || screens.welcome}
          </div>
        </div>
      </div>
    </>
  );
}
